-            angular.forEach(ip.routedTo, function (route) {
-                var vmPosition = _.indexOf(self.infra.vrack.publicCloud.sortedKeys, route);
-                if (vmPosition !== -1) {
-                    order += vmPosition * 5; // arbitrary weight of 5 for a link with a vm
-                    routeCount += 1;
