-                var values = ipRegex.exec(ip.ip);
-                var score = 0;
-                score += parseInt(values[1], 10) * 1000000000;
-                score += parseInt(values[2], 10) * 1000000;
-                score += parseInt(values[3], 10) * 1000;
-                score += parseInt(values[4], 10);
-                return score;
-            }
-            return ip.ip;
-        };
