+    }
+
+    // Universes
+    &-list_main {
+        .navbar-universe(web, @oui-color-jungle);
+        .navbar-universe(cloud, @oui-color-supernova);
+        .navbar-universe(telecom, @oui-color-dodger);
+        .navbar-universe(sunrise, @oui-color-california);
+        .navbar-universe(gamma, @oui-color-california);
+
+        .navbar-universe(@universe-selector, @universe-color) {
+            .@{universe-selector} > .oui-navbar-link {
+                &:hover,
+                &:focus,
+                &_active { color: @universe-color; }
+            }
+        }
+    }
+
+
+    // Languages menu
+    &-menu {
+        &_language &__item_active {
+            &,
+            &:hover,
+            &:active { background-color: @oui-navbar-menu-background-color_hover; }
+
+            .oui-navbar-link::after {
+                content: @oui-icon-index-check_line;
+                display: inline-block;
+                margin-left: rem-calc(20);
+                margin-top: -.3rem; // For a better centering
+                vertical-align: middle;
+                font-family: 'Oui Icons';
+                font-size: @oui-navbar-external-size;
+                color: inherit;
+            }
+        }
+    }
+
+    // Icons
+    .navbar-icon(icon-assistance);
+    .navbar-icon(icon-notifications);
+    .navbar-icon(icon-user);
+
+    .navbar-icon(@icon-selector) {
+        .navbar-@{icon-selector} {
+            background: url('../assets/images/navbar/@{icon-selector}.svg') no-repeat center center;
+            background-size: contain;
+        }
+
+        .oui-navbar-link  {
+            &:hover,
+            &:focus,
+            &_active,
+            &[aria-expanded="true"] {
+                .navbar-@{icon-selector} { background: url('../assets/images/navbar/@{icon-selector}_on.svg') no-repeat center center; }
+            }
+        }
+    }
+
+    // Logout
+    .logout > .oui-navbar-link {
+        &,
+        &:hover,
+        &:focus,
+        &_active {
+            font-weight: 600;
+            color: @oui-color-error-dark;
+        }
+    }
+}
+
